mapper.insert:
INSERT INTO ITEM(ITEM_ID,
                 ITEM_NAME,
                 ITEM_PRICE,
                 ITEM_QTY
                 )
VALUES (SEQ_ITEM.NEXTVAL,
        #{itemName},
        #{itemPrice},
        #{itemQty}
        )

mapper.update:
UPDATE ITEM
SET    ITEM_NAME =  #{itemName},
       ITEM_PRICE = #{itemPrice},
       ITEM_QTY = #{itemQty}
WHERE ITEM_ID = #{itemId}
